var title_f27_42_28331="Indications for liver biopsy";
var content_f27_42_28331=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F77571&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F77571&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Indications for liver biopsy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Diagnosis, grading, and staging of alcoholic liver disease, nonalcoholic steatohepatitis, and autoimmune hepatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grading and staging of chronic hepatitis B and C",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Diagnosis of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          Hemochromatosis (quantitative estimation of hepatic iron)",
"         </li>",
"         <li>",
"          Wilson's disease (quantitative hepatic copper)",
"         </li>",
"         <li>",
"          Focal liver lesions",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Evaluation of:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <ul>",
"         <li>",
"          Cholestatic liver disease: primary biliary cirrhosis, primary sclerosing cholangitis",
"         </li>",
"         <li>",
"          Abnormal liver biochemical tests in a patient with a negative or inconclusive serologic work-up",
"         </li>",
"         <li>",
"          Treatment efficacy",
"         </li>",
"         <li>",
"          Side effects of treatment regimens (such as methotrexate for rheumatoid arthritis)",
"         </li>",
"         <li>",
"          Post liver transplant by protocol or for evaluation of abnormal liver biochemical tests",
"         </li>",
"         <li>",
"          Donor liver",
"         </li>",
"         <li>",
"          Fever of unknown origin",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Bravo AA, Sheth SG, Chopra S. Liver biopsy. N Engl J Med 2001; 344:495.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
