var title_f27_42_28332="Features of hemorrhage I";
var content_f27_42_28332=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F72772&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F72772&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features of major causes of immune or idiopathic alveolar hemorrhage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Extrapulmonary features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Serology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Essential diagnostic criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        ABMA disease",
"       </td>",
"       <td>",
"        GN &gt;90%",
"       </td>",
"       <td rowspan=\"2\">",
"        +ABMA",
"       </td>",
"       <td>",
"        +Serum ABMA",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extrarenal disease rare",
"       </td>",
"       <td>",
"        Linear IgG in glomeruli",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        SLE",
"       </td>",
"       <td>",
"        GN &gt;90%",
"       </td>",
"       <td rowspan=\"2\">",
"        +ANA",
"       </td>",
"       <td rowspan=\"2\">",
"        Clinical features, +ANA, immune complexes in glomeruli by IF, EM",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Extrarenal disease common (arthritis, serositis, purpura, photosensitivity)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        WG",
"       </td>",
"       <td class=\"sublist1_start\">",
"        GN &gt;90% extrarenal disease",
"       </td>",
"       <td rowspan=\"3\">",
"        +ANCA",
"       </td>",
"       <td rowspan=\"3\">",
"        <p>",
"         Necrotizing granulomatous vasculitis in upper airway or lung biopsy",
"        </p>",
"        <p>",
"         or",
"        </p>",
"        <p>",
"         Pauci-immune necrotizing GN with extensive upper airway disease or cavitary lung nodules",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Distinctive: upper airway disease, pulmonary nodules&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Nonspecific (arthritis, purpura, mononeuropathy)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Nonspecific systemic necrotizing vasculitis (microscopic polyarteritis, polyangiitis, overlap syndrome)",
"       </td>",
"       <td>",
"        GN &gt;90% extrarenal disease variably present (arthritis, purpura, mononeuropathy)",
"       </td>",
"       <td>",
"        &plusmn;ANCA (usually + if include p-ANCA)",
"       </td>",
"       <td>",
"        Necrotizing, crescentic GN (or pathologic evidence of vasculitis elsewhere) and exclude WG to extent possible",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Idiopathic pulmonary hemosiderosis",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        Exclusion of all other causes",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     ABMA: antibasement membrane antibody; GN: glomerulonephritis; SLE: systemic lupus erythematosus; ANA: antinuclear antibody; IF: immunofluorescence; EM: electron microscopy; WG: granulomatosis with polyangiitis (Wegener's); ANCA: antineutrophil cytoplasmic antibody; p-ANCA: perinuclear ANCA; %: percent.",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from: Leatherman JL. In: Immunologically Mediated Pulmonary Diseases, Lynch JP, DeRemee RA (Eds), JB Lippincott, Philadelphia, 1991, p.490.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
