var title_f27_43_28347="Rabies postexposure prophylaxis";
var content_f27_43_28347=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F70600&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F70600&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rabies postexposure prophylaxis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Vaccination category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Biologic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Schedule",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        <strong>",
"         Not previously vaccinated",
"        </strong>",
"       </td>",
"       <td>",
"        RIG",
"       </td>",
"       <td>",
"        Total dose is 20 units/kg body weight. As much of the full dose as feasible should be infiltrated around the wound(s) and any remaining given IM.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vaccine",
"       </td>",
"       <td>",
"        Human diploid cell vaccine (HDCV) or purified chick embryo cell vaccine (PCECV) 1 mL, IM (deltoid area), 1 each on days 0, 3, 7 and 14*",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <strong>",
"         Previously vaccinated",
"        </strong>",
"       </td>",
"       <td>",
"        RIG",
"       </td>",
"       <td>",
"        Not indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vaccine",
"       </td>",
"       <td>",
"        HDCV or PCECV 1 mL, IM (deltoid area), 1 each on days 0 and 3",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     RIG: rabies immune globulin.",
"     <br>",
"      * For persons with immunosuppression, rabies postexposure prophylaxis should be administered using all five doses of vaccine on days 0, 3, 7, 14, and 28.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Rupprecht CE, Briggs D, Brown CM, et al. Use of a reduced (4-dose) vaccine schedule for postexposure prophylaxis to prevent human rabies: recommendations of the advisory committee on immunization practices. MMWR Recomm Rep 2010; 59:1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
