var title_f27_43_28349="CF nutrition tests";
var content_f27_43_28349=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56711&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56711&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Laboratory monitoring of nutritional status for children with cystic fibrosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"5\" width=\"20%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"3\">",
"        Recommended frequency",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Tests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        At diagnosis",
"       </td>",
"       <td class=\"subtitle2\">",
"        Annually",
"       </td>",
"       <td class=\"subtitle2\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Beta carotene",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        At physician's discretion",
"       </td>",
"       <td>",
"        Serum levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin A",
"       </td>",
"       <td class=\"centered\">",
"        X*",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        Also check serum retinol binding protein and retinyl esters in patients with liver disease",
"       </td>",
"       <td>",
"        Vitamin A (retinol)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin D",
"       </td>",
"       <td class=\"centered\">",
"        X*",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        25-OH-D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin E",
"       </td>",
"       <td class=\"centered\">",
"        X*",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &alpha;-tocopherol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin K",
"       </td>",
"       <td class=\"centered\">",
"        X*",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        If patient has hemoptysis or hematemesis; in patients with liver disease",
"       </td>",
"       <td>",
"        PIVKA-II (preferably) or prothrombin time",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Essential fatty acids",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Consider checking in infants or those with FTT",
"       </td>",
"       <td>",
"        <p>",
"         Triene:tetraene ratio",
"        </p>",
"        or Linoleic acid (as moles percent of total serum phospholipid fatty acids)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcium/bone status",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        Begin DXA at age 8 years if risk factors are present",
"        <sup>",
"         &bull;",
"        </sup>",
"        and repeat every 1 to 2 years depending on results (see text)",
"       </td>",
"       <td>",
"        Calcium, phosphorus, intact PTH, DXA scan",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iron",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        Consider in-depth evaluation for patients with poor appetite",
"       </td>",
"       <td>",
"        <p>",
"         Hemoglobin, hematocrit",
"        </p>",
"        Serum transferrin receptor (sTfR)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zinc",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Consider 6-month supplementation trial and follow growth",
"       </td>",
"       <td>",
"        No acceptable measurement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sodium",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Consider checking if exposed to heat stress and becomes dehydrated",
"       </td>",
"       <td>",
"        Serum sodium; spot urine sodium if total body sodium depletion suspected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Protein stores",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        Check in patients with nutritional failure or those at risk",
"       </td>",
"       <td>",
"        Albumin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oral glucose tolerance test",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        &nbsp;",
"       </td>",
"       <td class=\"centered\">",
"        X",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        HgbA1c &ge;6.5 percent or fasting plasma glucose &ge;126 mg/dL support the diagnosis of CFRD but are not sufficiently sensitive to use for screening",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     25-OH-D: 25-hydroxyvitamin D; PIVKA: proteins induced by vitamin K absence or antagonism; FTT: failure to thrive; Intact PTH: intact parathyroid hormone; DXA: dual energy x-ray absorptiometry; HgbA1c: hemoglobin A1c.",
"     <br/>",
"     * For infants diagnosed by neonatal screening these should be measured approximately 2 months after starting vitamin supplementation, and annually thereafter.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Risk factors for bone disease include body mass index (BMI) &lt;10th percentile for age, FEV1 &lt;50 percent predicted, glucocorticoid use of &ge;5 mg daily for &ge;90 days/year, delayed puberty, or a history of fractures.",
"     <br/>",
"     &Delta; Testing for cystic fibrosis-related diabetes (CFRD) using the oral glucose tolerance test (OGTT) should be performed annually in patients 10 years and older, and also in patients who develop symptoms of CFRD.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Borowitz D, Baker RD, Stallings V. Consensus report on nutrition for pediatric patients with cystic fibrosis. J Pediatr Gastroenterol Nutr 2002; 35:246. Copyright &copy; 2002 Lippincott Williams &amp; Wilkins.",
"     <br/>",
"     Additional recommendations on bone health from: Aris RM, et al. Guide to bone health and disease in cystic fibrosis. J Clin Endocrinol Metab 2005; 90:1888.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
