" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Sleepwalking and other parasomnias in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/51/28473/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/51/28473/contributors\">",
"     Suresh Kotagal, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/51/28473/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/51/28473/contributors\">",
"     Ronald D Chervin, MD, MS",
"    </a>",
"    <br/>",
"   </div>",
