" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Lipid lowering with diet or dietary supplements",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/51/28474/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/51/28474/contributors\">",
"     Christine C Tangney, PhD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/51/28474/contributors\">",
"     Robert S Rosenson, MD",
"    </a>",
"    <br/>",
"   </div>",
