"function TIMI_STEMI_fx() {",
"with(document.TIMI_STEMI_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1.checked){",
"Score = Score + 1;",
"}",
"if (cc2.checked){",
"Score = Score + 3;",
"}",
"if (cc3.checked){",
"Score = Score + 2;",
"}",
"if (cc4.checked){",
"Score = Score + 2;",
"}",
"if (cc5.checked){",
"Score = Score + 1;",
"}",
"if (cc6.checked){",
"Score = Score + 1;",
"}",
"if (cc7.checked){",
"Score = Score + 1;",
"}",
"if (cc8[0].checked){",
"Score = Score + 3;",
"}",
"if (cc8[1].checked){",
"Score = Score + 2;",
"}",
"if (cc8[2].checked){",
"Score = Score + 0;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 0)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 1) && (Score <= 1)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 2) && (Score <= 2)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if ((Score >= 3) && (Score <= 3)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"if ((Score >= 4) && (Score <= 4)){ document.getElementById('rr1_5').bgColor = '#cccccc';",
"}",
"if ((Score >= 5) && (Score <= 5)){ document.getElementById('rr1_6').bgColor = '#cccccc';",
"}",
"if ((Score >= 6) && (Score <= 6)){ document.getElementById('rr1_7').bgColor = '#cccccc';",
"}",
"if ((Score >= 7) && (Score <= 7)){ document.getElementById('rr1_8').bgColor = '#cccccc';",
"}",
"if ((Score >= 8) && (Score <= 8)){ document.getElementById('rr1_9').bgColor = '#cccccc';",
"}",
"if ((Score >= 9) && (Score <= 14)){ document.getElementById('rr1_10').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
