var title_f27_53_28508="Causes of occupational asthma";
var content_f27_53_28508=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F66185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F66185&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Major causes of occupational asthma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Occupation at risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Low molecular weight chemicals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Isocyanates (eg, toluene diisocyanate, diphenylmethane diisocyanate, hexamethylene diisocyanate, naphthalene diisocyanate)",
"       </td>",
"       <td>",
"        Polyurethane workers, roofers, insulators, painters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anhydrides (eg, trimellitic anhydride, phthalic anhydride)",
"       </td>",
"       <td>",
"        Manufacturers of paint, plastics, epoxy resins",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Metals (eg, chromic acid, potassium dichromate, nickel sulfate, vanadium, platinum salts)",
"       </td>",
"       <td>",
"        Platers, welders, metal and chemical workers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Drugs (eg, beta lactam agents, opiates, other)",
"       </td>",
"       <td>",
"        Pharmaceutical workers, farm workers, health professionals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Wood dust (eg, Western red cedar, maple, oak, exotic woods)",
"       </td>",
"       <td>",
"        Carpenters, woodworkers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dyes and bleaches (eg, anthraquinone, carmine, henna extract, persulfate, reactive dyes)",
"       </td>",
"       <td>",
"        Fabric and fur dyers, hairdressers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amines",
"       </td>",
"       <td>",
"        Chemists, cleaners, plastic manufacturers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Glues and resins (eg, acrylates, epoxy)",
"       </td>",
"       <td>",
"        Plastic manufacturers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Miscellaneous (eg, formaldehyde, glutaraldehyde, ethylene oxide, pyrethrin, polyvinyl chloride vapor)",
"       </td>",
"       <td>",
"        Laboratory workers, textile workers, paint sprayers, health professionals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        High molecular weight organic materials",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Animal proteins (eg, domestic and laboratory animals, fish and seafood)",
"       </td>",
"       <td>",
"        Farmers, veterinarians, poultry processors, fish and seafood processors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Flours and cereals",
"       </td>",
"       <td>",
"        Bakers, food processors, dock workers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Enzymes (eg, pancreatic extracts, papain, trypsin, Bacillus subtilis, bromelain, pectinase, amylase, lipase)",
"       </td>",
"       <td>",
"        Bakers, food processors, pharmaceutical workers, plastic workers, detergent manufacturers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Plant proteins (eg, wheat, grain dust, coffee beans, tobacco dust, cotton, tea, latex, psyllium, various flours)",
"       </td>",
"       <td>",
"        Bakers, farmers, food and plant processors, health professionals, textile workers",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
