var title_f27_53_28509="Antibiotic regimens for tuboovarian abscess";
var content_f27_53_28509=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F82240&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F82240&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antibiotic regimens for tuboovarian abscess",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Regimens",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose (adult)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_start\" colspan=\"2\">",
"        <strong>",
"         First choice",
"        </strong>",
"        *",
"        <sup>",
"         [1-4]",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cefoxitin",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        2 g IV every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doxycycline",
"       </td>",
"       <td>",
"        100 mg orally or IV every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cefotetan",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        2 g IV every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Doxycycline",
"       </td>",
"       <td>",
"        100 mg orally or IV every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clindamycin",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        900 mg IV every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gentamicin",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        2 mg/kg loading dose then 1.5 mg/kg every 8 hours IV or IM",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ampicillin",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        2 g IV every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clindamycin",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        900 mg IV every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gentamicin (3-antibiotic regimen)",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td>",
"        2 mg/kg loading dose then 1.5 mg/kg every 8 hours IV or IM",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ampicillin-sulbactam",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        3 g IV every 6 hours",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Doxycycline",
"       </td>",
"       <td>",
"        100 mg orally or IV every 12 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Second choice",
"        </strong>",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Levofloxacin",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        500 mg IV once daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Metronidazole",
"       </td>",
"       <td>",
"        500 mg IV every 8 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Imipenem-cilastatin",
"       </td>",
"       <td>",
"        500 mg IV every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"2\">",
"        <strong>",
"         Outpatient oral regimens",
"        </strong>",
"        (",
"        <strong>",
"         only",
"        </strong>",
"        as continuation of therapy in select patients)",
"        <sup>",
"         &loz;&sect;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Levofloxacin",
"        <strong>",
"         or",
"        </strong>",
"        Ofloxacin",
"        <strong>",
"         and",
"        </strong>",
"       </td>",
"       <td>",
"        500 mg orally once daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Metronidazole",
"       </td>",
"       <td>",
"        500 mg orally twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"2\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Amoxicillin-clavulanate XR",
"       </td>",
"       <td>",
"        2000 mg extended release orally twice daily",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Antibiotic doses should be adjusted appropriately for patients with renal insufficiency or other dose-related consideration.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Alternatively gentamicin may be given 3 to 5 mg/kg IV once per day.",
"     <br/>",
"     &Delta; 3 g ampicillin-sulbactam is equivalent to 2 g ampicillin with 1 gram sulbactam.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     There are insufficient high quality data about this agent or regimen; coverage is theoretic.",
"     <br/>",
"     &sect; In select patients in whom clinical improvement has been clearly documented, continuation of therapy as an outpatient may be appropriate. Direct communication with the patient and a plan for follow-up must also be confirmed.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Workowski KA, Berman S. Centers for Disease Control and Prevention. Sexually transmitted disease treatment guidelines, 2010. MMWR Recomm Rep 2010; 59:1.",
"      </li>",
"      <li>",
"       Landers DV, Sweet RL. Tubo-ovarian abscess: contemporary approach to management. Rev Infect Dis 1983; 5:876.",
"      </li>",
"      <li>",
"       Reed SD, Landers DV, Sweet RL. Am J Obstet Gynecol 1991; 164:1556.",
"      </li>",
"      <li>",
"       Ginsburg DS, Stern JL, Hamod KA, et al. Am J Obstet Gynecol 1980; 138:1055.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
