" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    OARS multidimensional functional assessment questionnaire: Community version",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\" colspan=\"3\">",
"        Social resources",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        1. Are you single (never married), married, widowed, divorced, or separated?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1",
"       </td>",
"       <td colspan=\"2\">",
"        Single (never married)",
"       </td>",
"      </tr>",
"      <tr>",
