" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Measurement and clinical significance of antinuclear antibodies",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/54/28520/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/54/28520/contributors\">",
"     Morris Reichlin, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/54/28520/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?27/54/28520/contributors\">",
"     Robert H Shmerling, MD",
"    </a>",
"    <br/>",
"   </div>",
