var title_f27_54_28523="DSM-IV-TR criteria for substance abuse";
var content_f27_54_28523=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PSYCH%2F74334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PSYCH%2F74334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    DSM-IV-TR criteria for substance abuse",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        A. A maladaptive pattern of substance use leading to clinically significant impairment or distress, as manifested by one (or more) of the following, occurring within a 12-month period:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recurrent substance use resulting in a failure to fulfill major role obligations at work, school, or home (eg, repeated absences or poor work performance related to substance use; substance-related absences, suspensions, or expulsions from school; neglect of children or household)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recurrent substance use in situations in which it is physically hazardous (eg, driving an automobile or operating a machine when impaired by substance use)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recurrent substance-related legal problems (eg, arrests for substance-related disorderly conduct)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Continued substance use despite having persistent or recurrent social or interpersonal problems caused or exacerbated by the effects of the substance (eg, arguments with spouse about consequences of intoxication, physical fights)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         B. The symptoms have never met the criteria for Substance Dependence for this class of substance.",
"        </strong>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted with permission from the Diagnostic and Statistical Manual of Mental Disorders, Text Revision, Fourth Edition. Copyright &copy; 2000 American Psychiatric Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
