var title_f27_54_28524="Description of genital ulcer II";
var content_f27_54_28524=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F68988&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F68988&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of sexually transmitted genital ulcers",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Primary syphilis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Genital herpes",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chancroid",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lymphogranuloma venereum",
"       </td>",
"       <td class=\"subtitle1\">",
"        Donovanosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain",
"       </td>",
"       <td>",
"        Painless; may become tender if secondarily infected.",
"       </td>",
"       <td>",
"        Common; more prominent with initial infection than with recurrences.",
"       </td>",
"       <td>",
"        Common",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inguinal lymphadenopathy",
"       </td>",
"       <td>",
"        Unilateral or bilateral; firm, movable, and nontender; do not suppurate.",
"       </td>",
"       <td>",
"        Usually bilateral, firm, and tender; more common in primary episodes than in recurrences.",
"       </td>",
"       <td>",
"        Unilateral (rarely bilateral); overlying erythema; matted, fixed, and tender; may suppurate.",
"       </td>",
"       <td>",
"        Unilateral or bilateral; initially movable, firm, and tender; later indolent; fixed and matted; \"sign of Groove\" may suppurate; fistulas.",
"       </td>",
"       <td>",
"        Pseudobuboes; subcutaneous perilymphatic granulamotous lesions that produce inguinal swelling.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Constitutional symptoms",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Common in primary episode; less likely in recurrences.",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Frequent",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Course of disease if untreated",
"       </td>",
"       <td>",
"        Slowly resolves to latency (2 - 6 weeks).",
"       </td>",
"       <td>",
"        Typically recurs.",
"       </td>",
"       <td>",
"        May progress to erosive lesions.",
"       </td>",
"       <td>",
"        Local lesions heal; systemic disease may progress; disfigurement; late complications.",
"       </td>",
"       <td>",
"        Worsens slowly.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diagnostic tests",
"       </td>",
"       <td>",
"        Darkfield exam, direct immunofluorescence, FTA-ABS, VDRL.",
"       </td>",
"       <td>",
"        Tzanck smear, culture, Pap smear, direct immunofluorescence, electron-microscopy, direct immunoperoxidase staining, serology.",
"       </td>",
"       <td>",
"        Culture, biopsy (rarely done); Gram stained smears have low specificity.",
"       </td>",
"       <td>",
"        Lymphogranuloma venereum (LGV) complement fixation test; isolation of the microorganism by culture.",
"       </td>",
"       <td>",
"        \"Donovan bodies\" in tissue smears; biopsy.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted with permission from Martin DH, Mroczkowski TF: Sexually Transmitted Diseases. In: The Skin and Infection: A Color Atlas and Text. C. V. Sanders and LT Nesbitt, eds. Baltimore, Maryland: Williams &amp; Wilkins, 1995, p. 95.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
