" <div id=\"innerOutline\">",
"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F187708\">",
"      Brand Names: U.S.",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F187709\">",
"      Brand Names: Canada",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F187741\">",
"      Pharmacologic Category",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F187712\">",
"      Dosing: Adult",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F187713\">",
"      Dosing: Geriatric",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F16160293\">",
"      Dosing: Renal Impairment",
"     </a>",
