var title_f27_56_28558="Drugs interact theophylline I";
var content_f27_56_28558=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F65611%7EPULM%2F78641&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F65611%7EPULM%2F78641&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=12\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinically significant drug interactions with theophylline",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type of interaction",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effect on theophylline serum",
"concentrations or pharmacologic effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adenosine",
"       </td>",
"       <td>",
"        Theophylline blocks adenosine receptors",
"       </td>",
"       <td>",
"        Higher doses of adenosine may be required to achieve",
"desired anti-arrhythmic effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alcohol",
"       </td>",
"       <td>",
"        A single large dose of alcohol (3 ml/kg of whiskey)",
"decreases theophylline clearance for up to 24 hours",
"       </td>",
"       <td>",
"        33 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Allopurinol",
"       </td>",
"       <td>",
"        Decreases theophylline clearance at allopurinol doses &ge;600 mg/day",
"       </td>",
"       <td>",
"        25 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aminoglutethimide",
"       </td>",
"       <td>",
"        Increases theophylline clearance by induction of",
"microsomal enzyme activity",
"       </td>",
"       <td>",
"        25 percent decrease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Carbamazepine",
"       </td>",
"       <td>",
"        Similar to aminoglutethimide",
"       </td>",
"       <td>",
"        30 percent decrease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cimetidine",
"       </td>",
"       <td>",
"        Decreases theophylline clearance by inhibiting",
"cytochrome P450 1A2",
"       </td>",
"       <td>",
"        70 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ciprofloxacin",
"       </td>",
"       <td>",
"        Similar to cimetidine",
"       </td>",
"       <td>",
"        40 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clarithromycin",
"       </td>",
"       <td>",
"        Similar to erythromycin",
"       </td>",
"       <td>",
"        25 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Diazepam",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Benzodiazepines increase CNS concentrations of",
"adenosine, a potent CNS depressant, while theophylline blocks adenosine",
"receptors",
"       </td>",
"       <td>",
"        Larger diazepam doses may be required to produce",
"desired level of sedation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discontinuation of theophylline without reduction of",
"diazepam dose may result in respiratory depression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Disulfiram",
"       </td>",
"       <td>",
"        Decreases theophylline clearance by inhibiting",
"hydroxylation and demethylation",
"       </td>",
"       <td>",
"        50 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Enoxacin",
"       </td>",
"       <td>",
"        Similar to cimetidine",
"       </td>",
"       <td>",
"        300 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ephedrine",
"       </td>",
"       <td>",
"        Synergistic CNS effects",
"       </td>",
"       <td>",
"        Increased frequency of nausea, nervousness, and insomnia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythromycin",
"       </td>",
"       <td>",
"        Erythromycin metabolite decreases theophylline",
"clearance by inhibiting cytochrome P450 3A3",
"       </td>",
"       <td>",
"        35 percent increase. Erythromycin steady-state serum",
"concentrations decrease by a similar amount",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Estrogen",
"       </td>",
"       <td>",
"        Estrogen-containing oral contraceptives decrease",
"theophylline clearance in a dose-dependent fashion. The effect of",
"progesterone on theophylline clearance is unknown",
"       </td>",
"       <td>",
"        30 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Flurazepam",
"       </td>",
"       <td>",
"        Similar to diazepam",
"       </td>",
"       <td>",
"        Similar to diazepam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fluvoxamine",
"       </td>",
"       <td>",
"        Similar to cimetidine",
"       </td>",
"       <td>",
"        Similar to cimetidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Halothane",
"       </td>",
"       <td>",
"        Halothane sensitizes the myocardium to catecholamines;",
"theophylline increases release of endogenous catecholamines",
"       </td>",
"       <td>",
"        Increased risk of",
"ventricular arrhythmias",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from Hendeles, L, Jenkins, J, Temple, R, Pharmacotherapy 1995; 15:409.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinically significant drug interactions with theophylline",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type of interaction",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effect on theophylline serum",
"concentrations or pharmacologic effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interferon, human recombinant alpha-A",
"       </td>",
"       <td>",
"        Decreases theophylline clearance",
"       </td>",
"       <td>",
"        100 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isoproterenol (IV)",
"       </td>",
"       <td>",
"        Increases theophylline clearance",
"       </td>",
"       <td>",
"        20 percent decrease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ketamine",
"       </td>",
"       <td>",
"        Pharmacologic",
"       </td>",
"       <td>",
"        May lower theophylline seizure threshold",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lithium",
"       </td>",
"       <td>",
"        Theophylline increases lithium renal clearance",
"       </td>",
"       <td>",
"        Lithium dose required to achieve a therapeutic serum",
"concentration increased an average of 60 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lorazepam",
"       </td>",
"       <td>",
"        Similar to diazepam",
"       </td>",
"       <td>",
"        Similar to diazepam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Methotrexate (MTX)",
"       </td>",
"       <td>",
"        Decreases theophylline clearance",
"       </td>",
"       <td>",
"        20 percent increase after low-dose MTX, higher dose MTX",
"may have a greater effect",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mexiletine",
"       </td>",
"       <td>",
"        Similar to disulfiram",
"       </td>",
"       <td>",
"        80 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Midazolam",
"       </td>",
"       <td>",
"        Similar to diazepam",
"       </td>",
"       <td>",
"        Similar to diazepam",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moricizine",
"       </td>",
"       <td>",
"        Increases theophylline clearance",
"       </td>",
"       <td>",
"        25 percent decrease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pancuronium",
"       </td>",
"       <td>",
"        Theophylline may antagonize non-depolarizing",
"neuromuscular blocking effects; possibly due to phosphodiesterase",
"inhibition",
"       </td>",
"       <td>",
"        Larger dose of pancuronium may be required to achieve",
"neuromuscular blockade",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pentoxifylline",
"       </td>",
"       <td>",
"        Decreases theophylline clearance",
"       </td>",
"       <td>",
"        30 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phenobarbital (PB)",
"       </td>",
"       <td>",
"        Similar to aminoglutethimide",
"       </td>",
"       <td>",
"        25 percent decrease after more than two weeks of",
"concurrent PB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Phenytoin",
"       </td>",
"       <td>",
"        Phenytoin increases theophylline clearance by",
"increasing microsomal enzyme activity",
"       </td>",
"       <td colspan=\"1\" rowspan=\"2\">",
"        Serum theophylline and phenytoin concentrations",
"decrease about 40 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Theophylline decreases phenytoin absorption",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Propafenone",
"       </td>",
"       <td>",
"        Decreases theophylline clearance and pharmacologic",
"interaction",
"       </td>",
"       <td>",
"        40 percent increase. Beta-2-blocking effect may",
"decrease efficacy of theophylline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Propranolol",
"       </td>",
"       <td>",
"        Similar to cimetidine and pharmacologic interaction",
"       </td>",
"       <td>",
"        100 percent increase. Beta-2-blocking effect may",
"decrease efficacy of theophylline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rifampin",
"       </td>",
"       <td>",
"        Increases theophylline clearance by increasing",
"cytochrome P450 1A2 and 3A3 activity",
"       </td>",
"       <td>",
"        20 to 40 percent decrease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sulfinpyrazone",
"       </td>",
"       <td>",
"        Increases theophylline clearance by increasing",
"demethylation and hydroxylation. Decreases renal clearance of",
"theophylline",
"       </td>",
"       <td>",
"        20 percent decrease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tacrine",
"       </td>",
"       <td>",
"        Similar to cimetidine, also increases renal clearance",
"of theophylline",
"       </td>",
"       <td>",
"        90 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thiabendazole",
"       </td>",
"       <td>",
"        Decreases theophylline clearance",
"       </td>",
"       <td>",
"        190 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ticlopidine",
"       </td>",
"       <td>",
"        Decreases theophylline clearance",
"       </td>",
"       <td>",
"        60 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Troleandomycin",
"       </td>",
"       <td>",
"        Similar to erythromycin",
"       </td>",
"       <td>",
"        33 to 100 percent increase depending on troleandomycin",
"dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Verapamil",
"       </td>",
"       <td>",
"        Similar to disulfiram",
"       </td>",
"       <td>",
"        20 percent increase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zafirlukast",
"       </td>",
"       <td>",
"        Mechanism not studied; theophylline decreases either",
"bioavailability or",
"clearance of zafirlukast. Zafirlukast has no effect on theophylline",
"clearance",
"       </td>",
"       <td>",
"        Zafirlukast blood levels decrease an average of 40",
"percent, which is likely to render it ineffective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Zileuton",
"       </td>",
"       <td>",
"        Zileuton inhibits cytochrome P-450 1A2 and, thus,",
"decreases theophylline clearance",
"       </td>",
"       <td>",
"        100 percent increase",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Redrawn from Hendeles, L, Jenkins, J, Temple, R, Pharmacotherapy 1995; 15:409.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
