var title_f27_57_28572="HBV immunization management of preterm infants";
var content_f27_57_28572=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F55139&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F55139&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Hepatitis B immunization management of preterm infants weighing &lt;2000 g, by maternal hepatitis B surface antigen (HBsAg) status",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Maternal HBsAg status",
"       </td>",
"       <td class=\"subtitle1\">",
"        Recommendation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Positive",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Administer HBIG + single-antigen hepatitis B vaccine within 12 hours of birth",
"         </li>",
"         <li>",
"          Do not count the birth dose as part of the vaccine series",
"         </li>",
"         <li>",
"          Administer three additional hepatitis B vaccine doses with",
"          <br/>",
"          - Single-antigen vaccine at ages 1, 2 to 3, and 6 months,",
"          <em>",
"           or",
"          </em>",
"          <br/>",
"          - Hepatitis B-containing combination vaccine at ages 2, 4, and 6 months (Pediarix) or 2, 4, and 12 to 15 months (Comvax)*",
"         </li>",
"         <li>",
"          Test for HBsAg and antibody to HBsAg 1 to 2 months after completion of &gt;3 doses of a licensed hepatitis B vaccine series (ie, at age 9 to 18 months, generally at the next well-child visit). Testing should not be performed before age 9 months nor within four weeks of the most recent vaccine dose.",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td class=\"indent1\">",
"        Unknown",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Administer HBIG + single-antigen hepatitis B vaccine within 12 hours of birth",
"         </li>",
"         <li>",
"          Test mother for HBsAg",
"         </li>",
"         <li>",
"          Do not count the birth dose as part of the vaccine series",
"         </li>",
"         <li>",
"          Administer three additional hepatitis B vaccine doses with",
"          <br/>",
"          - Single-antigen vaccine at ages 1, 2 to 3, and 6 months,",
"          <em>",
"           or",
"          </em>",
"          <br/>",
"          - Hepatitis B-containing combination vaccine at ages 2, 4, and 6 months (Pediarix) or 2, 4, and 12 to 15 months (Comvax)*",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Negative",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Delay first dose of hepatitis B vaccine until age 1 month or hospital discharge",
"         </li>",
"         <li>",
"          Complete the hepatitis B vaccine series with",
"          <br/>",
"          - Single-antigen vaccine at ages 2 months and 6 to 18 months,",
"          <em>",
"           or",
"          </em>",
"          <br/>",
"          - Hepatitis B-containing combination vaccine at ages 2, 4, and 6 months (Pediarix) or 2, 4, and 12 to 15 months (Comvax)*",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HBsAg: hepatitis B surface antigen; HBIG: hepatitis B immune globulin.",
"     <br/>",
"     * The final dose in the vaccine series should not be administered before age 24 weeks (164 days).",
"    </div>",
"    <div class=\"reference\">",
"     This table is a replacement for Table 4 from: Mast EE, Margolis HS, Fiore AE, et al. A comprehensive immunization strategy to eliminate transmission of hepatitis B virus infection in the United States. MMWR 2005; 54:1. A list of the major errata appeared in MMWR on December 7, 2007.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
