var title_f27_59_28603="Energy intake CKD children";
var content_f27_59_28603=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56276&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56276&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Equations to estimate energy requirements for children with chronic kidney disease at healthy weights",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Age",
"       </td>",
"       <td class=\"subtitle1\">",
"        Estimated energy requirement (EER) (kcal/day) = total energy expenditure + energy deposition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0 to 3 months",
"       </td>",
"       <td>",
"        EER = [89 x weight (kg) - 100] + 175",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 to 6 months",
"       </td>",
"       <td>",
"        EER = [89 x weight (kg) - 100] + 56",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        7 to 12 months",
"       </td>",
"       <td>",
"        EER = [89 x weight (kg) - 100] + 22",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        13 to 35 months",
"       </td>",
"       <td>",
"        EER = [89 x weight (kg) - 100] + 20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        3 to 8 years",
"       </td>",
"       <td>",
"        Boys: EER = 88.5 - 61.9 x age (years) + PA x [26.7 x weight (kg) + 903 x height (m)] + 20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Girls: EER = 135.3 - 30.8 x age (years) + PA x [10 x weight (kg) + 934 x height (m)] + 20",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        9 to 18 years",
"       </td>",
"       <td>",
"        Boys: EER = 88.5 - 61.9 x age (years) + PA x [26.7 x weight (kg) + 903 x height (m)] + 25",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Girls: EER = 135.3 - 30.8 x age (years) + PA x [10 x weight (kg) + 934 x height (m)] + 25",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: National Kidney Foundation. KDOQI Clinical Practice Guideline for Nutrition in Children with CKD: 2008 update. Executive summary. Am J Kidney Dis 2009; 53:S11. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
