var title_f27_6_27755="Heterozyg comp def";
var content_f27_6_27755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F72700&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F72700&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Inherited partial (heterozygous) defects and polymorphisms of complement proteins leading to human disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Defective protein(s)",
"      </td>",
"      <td class=\"subtitle1\">",
"       Associated illnesses",
"      </td>",
"      <td class=\"subtitle1\">",
"       Mechanism",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Factor H*",
"      </td>",
"      <td>",
"       aHUS; AMD; HELLP",
"      </td>",
"      <td>",
"       Excessive AP activation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Factor I*",
"      </td>",
"      <td>",
"       aHUS: HELLP",
"      </td>",
"      <td>",
"       Excessive AP activation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       MCP (CD46)*",
"      </td>",
"      <td>",
"       aHUS; HELLP",
"      </td>",
"      <td>",
"       Excessive AP activation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Factor B&bull;",
"      </td>",
"      <td>",
"       aHUS; AMD",
"      </td>",
"      <td>",
"       Excessive AP activation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       C3&bull;",
"      </td>",
"      <td>",
"       aHUS, AMD",
"      </td>",
"      <td>",
"       Excessive AP activation",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     aHUS: atypical hemolytic uremic syndrome; AMD: age-related macular degeneration; HELLP: hemolytic anemia, elevated liver enzymes and low platelet count; AP: alternative pathway; MCP: membrane cofactor protein.",
"     <br>",
"      * Decreased regulatory activity secondary to being haploinsufficient (lack of protein production or a functionally deficient protein)",
"      <br>",
"       &bull; Gain of function mutations lead to increased alternative pathway activity.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of M. Liszewski and Dr. John P. Atkinson.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
