var title_f27_6_27756="Radiation fields in HL";
var content_f27_6_27756=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F78652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F78652&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Definition of radiation fields in Hodgkin lymphoma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Anatomic definitions of separate lymph node regions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Waldeyer's ring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cervical, supraclavicular, occipital, and preauricular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infraclavicular",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Axillary and pectoral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mediastinal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hilar",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Epitrochlear and brachial",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Spleen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paraaortic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mesenteric",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iliac (pelvic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inguinal and femoral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Popliteal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Functional radiation field definitions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Involved field",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"1\">",
"        An involved field includes not only the individual clinically involved",
"or enlarged node(s) but also the other lymph nodes within the same",
"lymph node region (see above). The involved field is the minimum",
"radiation field size used for Hodgkin lymphoma.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Extended field",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        An extended field includes the involved field (see above) as well as all",
"        <strong>",
"         immediately",
"        </strong>",
"        adjacent lymph node regions.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Mantle field",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The mantle field includes all of the supradiaphragmatic lymph nodes commonly involved in Hodgkin lymphoma (see below).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Inverted-Y field",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The inverted-Y field is a combination of paraaortic, iliac (pelvic),",
"and inguinal-femoral fields, with or without the splenic field (see",
"below).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Total nodal irradiation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Total nodal irradiation implies treatment of all major lymph node",
"regions above and below the diaphragm; typically a mantle field plus an",
"inverted-Y field. It is important to note that certain lymph node",
"groups are rarely involved in Hodgkin lymphoma and are not included in",
"these fields. These include brachial, epitrochlear, popliteal, sacral",
"and mesenteric nodes.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
