var title_f27_60_28620="Risk factors for SIDS";
var content_f27_60_28620=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F75379&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F75379&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Epidemiologic risk factors for SIDS",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        General factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Low birth weight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Racial/ethnic differences (increased risk in nonwhite races)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gender - male greater than female",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Climate - twofold increased risk in cold versus warm months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Maternal and antenatal factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Smoking, illicit drugs&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Young, unmarried, no high school degree&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Late or no prenatal care&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poor gestational weight gain&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pregnancy complications (placenta previa, abruption, premature rupture of membranes)&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urinary tract infection, sexually transmitted disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short interpregnancy interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Neonatal factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prematurity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Small for gestational age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vital signs -",
"        <strong>",
"         not",
"        </strong>",
"        a risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Apnea of prematurity -",
"        <strong>",
"         not",
"        </strong>",
"        a risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Post neonatal factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prone sleep position - 1.3-fold increased risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sleep environment - soft sleep surfaces, loose bedding accessories, bed-sharing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recent gastrointestinal illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Listlessness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breast feeding -&nbsp;",
"        <strong>",
"         reduces",
"        </strong>",
"        risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recent upper respiratory infection -",
"        <strong>",
"         not",
"        </strong>",
"        a risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunizations -",
"        <strong>",
"         not",
"        </strong>",
"        a risk",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     SIDS: sudden infant death syndrome.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Hoffman HJ, Damus K, Hillman L, Krongrad E. Risk factors for SIDS. Results of the National Institute of Child Health and Human Development SIDS Cooperative Epidemiological Study. Ann N Y Acad Sci 1988; 533:13.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
