var content_f27_61_28635=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F70239&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F70239&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Primary disorders causing low HDL levels",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Disorder",
"       </td>",
"       <td class=\"subtitle1\">",
"        Lipid levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Confirmatory studies",
"       </td>",
"       <td class=\"subtitle1\">",
"        Physical findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial primary hypoalphalipoproteinemia",
"       </td>",
"       <td>",
"        HDL &lt;10th percentile in patient and in one first- degree or two second-degree relatives; TC and TG &lt;90th percentile; no secondary cause of low HDL",
"       </td>",
"       <td>",
"        &nbsp;",
"        <br/>",
"       </td>",
"       <td>",
"        Cataracts; corneal arcus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nonfamilial hypoalphalipoproteinemia",
"       </td>",
"       <td>",
"        Same as above except no relatives with HDL &lt;10th percentile",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial hypoalphalipoproteinemia with hypertriglyceridemia",
"       </td>",
"       <td>",
"        HDL &lt;10th percentile, TC &lt;90th percentile, and TG &gt;90th percentile in patient and in one first-degree or two second-degree relatives; no secondary cause of low HDL",
"       </td>",
"       <td>",
"        Reduced LPL activity",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Summary of diagnostic criteria for the different metabolic disorders responsible for low HDL levels.",
"    <div class=\"footnotes\">",
"     TC: total cholesterol; TG: total triglycerides; LPL: lipoprotein lipase.",
"    </div>",
"    <div class=\"reference\">",
"     Data from Rosenson RS, Frauenheim WA, Tangney CC. Disease-A-Month 1994; XL:369.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
