var title_f27_61_28636="Feminizing effects in male-to-female transsexual persons";
var content_f27_61_28636=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F54189&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F54189&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Feminizing effects in male-to-female transsexual persons",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Effect",
"       </td>",
"       <td class=\"subtitle1\">",
"        Onset*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Maximum*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Redistribution of body fat",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"       <td>",
"        2-3 yr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decrease in muscle mass and strength",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"       <td>",
"        1-2 yr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Softening of skin/decreased oiliness",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased libido",
"       </td>",
"       <td>",
"        1-3 months",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased spontaneous erections",
"       </td>",
"       <td>",
"        1-3 months",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Male sexual dysfunction",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Breast growth",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"       <td>",
"        2-3 yr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased testicular volume",
"       </td>",
"       <td>",
"        3-6 months",
"       </td>",
"       <td>",
"        2-3 yr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased sperm production",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"       <td>",
"        &gt;3 yr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased terminal hair growth",
"       </td>",
"       <td>",
"        6-12 months",
"       </td>",
"       <td>",
"        &gt;3 yr",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Scalp hair",
"       </td>",
"       <td>",
"        No regrowth",
"       </td>",
"       <td>",
"        &Delta;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Voice changes",
"       </td>",
"       <td>",
"        None",
"       </td>",
"       <td>",
"        &loz;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Estimates represent clinical observations.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Complete removal of male sexual hair requires electrolysis, or laser treatment, or both.",
"     <br/>",
"     &Delta; Familial scalp hair loss may occur if estrogens are stopped.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     Treatment by speech pathologists for voice training is most effective.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Hembree WC, Cohen-Kettenis P, Delemarre-van de Waal HA, et al. Endocrine treatment of transsexual persons: an Endocrine Society clinical practice guideline. J Clin Endocrinol Metab 2009; 94:3132. Copyright &copy; 2009 The Endocrine Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
