" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Therapeutic use of dofetilide",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?27/63/28663/contributors\">",
