var title_f27_8_27788="Clinical features distinguishing T1DM T2DM MODY";
var content_f27_8_27788=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F83072&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F83072&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features distinguishing type 1 diabetes, type 2 diabetes, and maturity onset diabetes of the young",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type 1 diabetes mellitus",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type 2 diabetes mellitus",
"       </td>",
"       <td class=\"subtitle1\">",
"        MODY",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age of diagnosis (years)",
"       </td>",
"       <td>",
"        Majority &lt;25, but may occur at any age",
"       </td>",
"       <td>",
"        Typically &gt;25 but incidence is increasing in adolescents, paralleling increasing rates of obesity in children and adolescents*",
"       </td>",
"       <td>",
"        &lt;25",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Weight",
"       </td>",
"       <td>",
"        Usually thin, but with obesity epidemic overweight and obesity at diagnosis becoming more common",
"       </td>",
"       <td>",
"        &gt;90 percent at least overweight",
"       </td>",
"       <td>",
"        Similar to general population",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autoantibodies",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Insulin dependent",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Insulin sensitivity",
"       </td>",
"       <td>",
"        Normal when controlled",
"       </td>",
"       <td>",
"        Decreased",
"       </td>",
"       <td>",
"        Normal (may be decreased if obese)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Family history of diabetes",
"       </td>",
"       <td>",
"        Infrequent (5 to 10 percent)",
"       </td>",
"       <td>",
"        Frequent (75 to 90 percent)",
"       </td>",
"       <td>",
"        Multigenerational, ie, &gt;2 generations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Risk of diabetic ketoacidosis",
"       </td>",
"       <td>",
"        High",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"       <td>",
"        Low",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MODY: maturity onset diabetes of the young.",
"     <br/>",
"     * In North America, type 2 diabetes predominates in Hispanic, African-American, Native American, Canadian First Nation, Pacific Islander, and Asian-American youth.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Naylor R, Philipson LH. Who should have genetic testing for maturity-onset diabetes of the young? Clin Endocrinol (Oxf) 2011; 75:422.",
"      </li>",
"      <li>",
"       Ramesh SC, Marshall I. Clinical Suspicion of Maturity Onset of Diabetes of the Young in Pediatric Patients Diagnosed with Diabetes Mellitus. Indian J Pediatr 2011; Dec 10 [ePub].",
"      </li>",
"      <li>",
"       Thanabalasingham G, Owen KR. Diagnosis and management of maturity onset diabetes of the young (MODY). BMJ 2011; 343:d6044.",
"      </li>",
"      <li>",
"       Pinhas-Hamiel O, Zeitler P. The global spread of type 2 diabetes mellitus in children and adolescents. J Pediatr 2005; 146:693.",
"      </li>",
"      <li>",
"       De Ferranti  SD, Osganian SK. Epidemiology of paediatric metabolic syndrome and type 2 diabetes mellitus. Diab Vasc Dis Res 2007; 4:285.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
