" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial treatment of acute promyelocytic leukemia in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/11/28858/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/11/28858/contributors\">",
"     Richard A Larson, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/11/28858/contributors\">",
"     Section Editor",
"    </a>",
