var title_f28_12_28875="Surgery prosthetic valve IE";
var content_f28_12_28875=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F54251&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F54251&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Surgery for prosthetic valve endocarditis (PVE)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general agreement that surgery is indicated in patients with PVE who develop one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Valve dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Dehiscence seen by cine fluoroscopy or echocardiography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Evidence of increasing valve obstruction or worsening regurgitation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Complications such as abscess formation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class IIa - The weight of evidence or opinion is in favor of the usefulness of surgery in patients with PVE who develop one of the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Persistent bacteremia or recurrent emboli despite appropriate antibiotic therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Relapsing infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - There is evidence and/or general agreement that surgery is NOT indicated in patients with PVE in the following setting:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull; Uncomplicated IE caused by a first infection with a sensitive organism",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
