var title_f28_14_28908="Priority groups for flu vaccine if supply limited";
var content_f28_14_28908=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F79334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F79334&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    When influenza vaccine supply is limited, vaccination efforts should focus on individuals who:",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Are at high risk for influenza-related complications, including those who:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are 6 months through 4 years (59 months) of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are &ge;50 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have chronic pulmonary (including asthma), cardiovascular (except hypertension), renal, hepatic, hematologic (including sickle cell disease), metabolic (including diabetes mellitus), neurologic, neuromuscular, and neurodevelopmental disorders (including disorders of the brain, spinal cord, peripheral nerve and muscle such as cerebral palsy, epilepsy, stroke, intellectual disability [mental retardation], moderate to severe developmental delay, muscular dystrophy, or spinal cord injury)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are immunosuppressed (including immunosuppression caused by medications or by human immunodeficiency virus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are or will be pregnant during the influenza season",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are 6 months through 18 years of age and receiving long-term aspirin therapy (and may be at risk for Reye syndrome after influenza virus infection)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are residents of nursing homes and other chronic-care facilities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are Native Americans, including Alaska Natives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Are morbidly obese (body mass index [BMI] &ge;40 for adults or BMI &gt;2.33 standard deviations above the mean for children)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Live with or care for persons at high risk of influenza-related complications, including:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Healthcare personnel",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Household contacts or caregivers of children &lt;5 years and adults &ge;50 years of age, with particular emphasis on contacts of children &lt;6 months",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Household contacts or caregivers of persons with medical conditions that put them at increased risk for severe complications of influenza",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from:",
"     <br>",
"      <ol>",
"       <li>",
"        Centers for Disease Control and Prevention. 2011-2012 Influenza antiviral medications: Summary for clinicians. file://www.cdc.gov/flu/professionals/antivirals/summary-clinicians.htm (Accessed on September 05, 2011).",
"       </li>",
"       <li>",
"        Centers for Disease Control and Prevention (CDC). Prevention and control of influenza with vaccines: recommendations of the Advisory Committee on Immunization Practices (ACIP), 2011. MMWR Morb Mortal Wkly Rep 2011; 60:1128.",
"       </li>",
"       <li>",
"        Fiore AE, Uyeki TM, Broder K, et al. Prevention and control of influenza with vaccines: recommendations of the Advisory Committee on Immunization Practices (ACIP), 2010. MMWR Recomm Rep 2010; 59:1.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
