var title_f28_15_28924="Uses of noninvasive ventilation";
var content_f28_15_28924=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71141&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71141&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common uses of noninvasive ventilatory support",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"       Ventilator",
"      </td>",
"      <td class=\"subtitle1\" colspan=\"2\" rowspan=\"1\">",
"       Typical settings",
"      </td>",
"      <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"       Applications",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       Rate/min",
"      </td>",
"      <td class=\"subtitle2\">",
"       Pressures, cmH2O",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Positive pressure",
"      </td>",
"      <td>",
"       12-22",
"      </td>",
"      <td>",
"       10 to 20 inspiratory 3 to 5 expiratory 12 to 15 mL/kg",
"      </td>",
"      <td>",
"       Mode of first choice. Nasal route preferred, oronasal or mouthpiece if nasal unsuccessful.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"4\" rowspan=\"1\">",
"       Negative pressure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Iron lung",
"      </td>",
"      <td class=\"sublist1\">",
"       12-14",
"      </td>",
"      <td class=\"sublist1\">",
"       -12 to -25",
"      </td>",
"      <td class=\"sublist1\">",
"       Second line for patients if nasal ventilation fails.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Wrap",
"      </td>",
"      <td class=\"sublist1\">",
"       14-18",
"      </td>",
"      <td class=\"sublist1\">",
"       -20 to -40",
"      </td>",
"      <td class=\"sublist1\">",
"       Sleep apnea should be excluded first.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Shell or cuirass",
"      </td>",
"      <td class=\"sublist1\">",
"       14-18",
"      </td>",
"      <td class=\"sublist1\">",
"       -20 to -40",
"      </td>",
"      <td class=\"sublist1\">",
"       &nbsp;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"4\" rowspan=\"1\">",
"       Abdominal ventilator",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Pneumobelt",
"      </td>",
"      <td class=\"sublist_other\">",
"       16-22",
"      </td>",
"      <td class=\"sublist_other\">",
"       35 to 50",
"      </td>",
"      <td class=\"sublist_other\">",
"       Diaphragm paralysis or weakness; high cord lesion; mainly for daytime use.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rocking bed",
"      </td>",
"      <td>",
"       12-16",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Diaphragm paralysis or weakness; mainly for nocturnal use.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Glossopharyngeal breathing",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Respiratory muscle weakness, but upper airway function intact; post-polio and muscular dystrophy patients.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Diaphragm pacing",
"      </td>",
"      <td>",
"       12-14",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Intact phrenic nerve and diaphragm muscle; high cord lesion and central hypoventilation.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Expiratory aids",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       To augment peak cough flows in patients with weakened expiratory muscles.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
