" <!-- TC:TOPIC_PAGE -->",
" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Definition and etiology of fever in neonates and infants (less than three months of age)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Hannah F Smitherman, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Charles G Macias, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Stephen J Teach, MD, MPH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Morven S Edwards, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/17/28951/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/17/28951/contributors\">",
"     James F Wiley, II, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"UTD.htm?28/17/28951/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
