var title_f28_18_28971="Complement fragment functions";
var content_f28_18_28971=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F65948&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F65948&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=8\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Activated complement fragments: Deposition and function on cellular targets",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Activation fragment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Number on a single cell",
"       </td>",
"       <td class=\"subtitle1\">",
"        Notes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C4b",
"       </td>",
"       <td>",
"        2.5 million",
"       </td>",
"       <td>",
"        Cleavage by C1s generates C4b; C4b covalently binds to target",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C4bC2a",
"       </td>",
"       <td>",
"        0.5 million",
"       </td>",
"       <td>",
"        C3 convertase; one in five C4bs forms a convertase",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C3b",
"       </td>",
"       <td>",
"        20 million",
"       </td>",
"       <td>",
"        ~10x more C3b than C4b attaches to target (opsonization)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        C5b-C9",
"       </td>",
"       <td>",
"        1 million",
"       </td>",
"       <td>",
"        Membrane perturbation including lysis by the membrane attack complex",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    These data were obtained from quantitative analysis of IgG antibody-mediated classical pathway activation on mammalian cells (melanoma cell line). Normal human serum was used as the source of complement, along with saturating amounts of human antibody to the cell line. Similar data have been obtained on bacterial membranes, although relative numbers were less (approximately a log) corresponding to reduced surface area and antigenic density. The complement system is the \"guardian of the intravascular space.\" It is designed to attack bacteria attempting to enter the intravascular space.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
