var title_f28_19_28988="PPI recommended doses";
var content_f28_19_28988=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F65374&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F65374&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for PPI doses in the treatment of acid-related disorders",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dose (adult) oral",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Active and maintenance therapy of gastroduodenal ulcers*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dexlansoprazole",
"       </td>",
"       <td>",
"        30 to 60 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Esomeprazole",
"       </td>",
"       <td>",
"        20 to 40 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lansoprazole",
"       </td>",
"       <td>",
"        15 to 30 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Omeprazole",
"       </td>",
"       <td>",
"        20 to 40 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pantoprazole",
"       </td>",
"       <td>",
"        20 to 40 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rabeprazole",
"       </td>",
"       <td>",
"        20 mg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        All administered daily before breakfast",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Primary and secondary prevention of NSAID-induced ulcers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"2\">",
"        All PPIs as above",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Treatment of erosive or nonerosive gastroesophageal reflux disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dexlansoprazole",
"       </td>",
"       <td>",
"        30 mg daily or 30 mg twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Esomeprazole",
"       </td>",
"       <td>",
"        20 or 40 mg daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lansoprazole",
"       </td>",
"       <td>",
"        30 mg daily or 30 mg twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Omeprazole",
"       </td>",
"       <td>",
"        20 to 40 mg daily or 20 mg twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pantoprazole",
"       </td>",
"       <td>",
"        40 mg daily or 40 mg twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rabeprazole",
"       </td>",
"       <td>",
"        20 mg daily or 20 mg twice daily",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"2\">",
"        All administered daily before breakfast, second dose if necessary should be given before evening meal",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PPI: proton pump inhibitor; NSAID: nonsteroidal antiinflammatory drug.",
"     <br/>",
"     * As a general rule, active duodenal ulcers should be treated for four weeks and gastric ulcers for eight weeks.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Meals should ideally contain protein to enhance parietal cell stimulation.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Wolfe MM, Sachs G. Acid suppression: Optimizing therapy for gastroduodenal ulcer healing, gastroesophageal reflux disease, and stress-related erosive syndrome. Gastroenterology 2000; 118:S9.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
