var script_f28_21_29009=[" ",
"",
"",
"function log(i){",
"return Math.log(i) * Math.LOG10E;   ",
"}",
"",
"function ln(i){",
"return Math.log(i);",
"}",
"",
"function sq(i){",
"return i * i;",
"}",
"",
"function sqr(i){",
"return Math.sqrt(i);",
"}",
"",
"",
"function power(x,y){",
"return Math.pow(x,y);",
"}",
"",
"function eTo(x){",
"return Math.exp(x);",
"}",
"",
"",
"function fixDP(r, dps) {",
"if (isNaN(r)) return \"NaN\";",
"var msign = '';",
"if (r < 0) msign = '-';",
"x = Math.abs(r);",
"if (x > Math.pow(10, 21)) return msign + x.toString();",
"var m = Math.round(x * Math.pow(10, dps)).toString();",
"if (dps == 0) return msign + m;",
"while (m.length <= dps) m = \"0\" + m;",
"return msign + m.substring(0, m.length - dps) + \".\" + m.substring(m.length - dps);",
"}",
"",
"function alertNaN(){",
"alert('This field is improperly formatted. You may only input the digits 0-9 and a decimal point.');  ",
"doCalc = false; ",
"clrResults();",
"}",
"",
"",
"function validNumberField(event) {",
"var field = event.target;",
"",
"if(field && field != 'undefined' && field.attributes.type.nodeValue && (field.attributes.type.nodeValue == 'number')) {",
"var val = field.value;",
"var charCode = event.charCode;",
"var key = String.fromCharCode(charCode);",
"",
"if(charCode == 0) {",
"return true;",
"}",
"",
"if(key == ' ') {",
"alert('Numeric field cannot contain spaces.');",
"return false;",
"}	",
"		",
"if(val.length == 0) {",
"if(key == '+') { return true; }",
"if(key == '-') { return true; }	",
"if(key == '.') { return true; }",
"}",
"		",
"if(key == '.') { return validFloat(val, key); }",
"",
"if(isNaN(parseInt(key))) {",
"alertNaN();",
"return false;",
"}",
"	",
"return validFloat(val, key);",
"}",
"return true;",
"}",
"",
"",
"function validFloat(val, key) {",
"var currentVal = parseFloat(val + key);",
"if(isNaN(val + key) || isNaN(currentVal)) {",
"alertNaN();",
"}",
"return true;",
"}",
"",
"var currenttimeout;",
"",
"",
"var calctxt = ''; ",
"var xmltxt = ''; ",
"var htmtxt = ''; ",
"",
"",
"",
"function LeanBodyWgtM_fx() {",
"",
"currenttimeout = self.setTimeout('minMaxCheck();', 2000);",
"",
"with(document.LeanBodyWgtM_form){",
"",
"",
"doCalc = true;",
"param_value = parseFloat(Total_Body_Weight_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Total_Body_Weight_unit.options[Total_Body_Weight_unit.selectedIndex].value.split('|');",
"Total_Body_Weight = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"param_value = parseFloat(Height_param.value);",
"if (isNaN(param_value)){param_value = \"\"; doCalc = false;}",
"unit_parts = Height_unit.options[Height_unit.selectedIndex].value.split('|');",
"Height = param_value * parseFloat(unit_parts[0]) + parseFloat(unit_parts[1]);",
"dp = decpts.options[decpts.selectedIndex].text;",
"BMI =  Total_Body_Weight / sq(Height / 100);",
"",
"LBW =  (9270 * Total_Body_Weight) / (6680 + (216 * BMI));",
"",
"unit_parts = LBW_unit.options[LBW_unit.selectedIndex].value.split('|');",
"if (doCalc) LBW_param.value = fixDP((LBW - parseFloat(unit_parts[1])) / parseFloat(unit_parts[0]), dp);",
"",
"",
"",
"",
"",
"}",
"",
"}",
"",
"function minMaxCheck(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.LeanBodyWgtM_form){",
"",
"if (Total_Body_Weight_param.value && isNaN(Total_Body_Weight_param.value)){ Total_Body_Weight_param.value = ''; alertNaN(); }",
"if (Total_Body_Weight_param.value && Total_Body_Weight < 47) {",
"Total_Body_Weight = 0;",
"Total_Body_Weight_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Total Body Weight is 47 kg.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Height_param.value && isNaN(Height_param.value)){ Height_param.value = ''; alertNaN(); }",
"if (Height_param.value && Height < 137) {",
"Height = 0;",
"Height_param.value = \"\";",
"clrResults();",
"doCalc = false;",
"alert(\"The minimum value for Height is 137 cm.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"if (Height_param.value && Height > 244) {",
"Height_param.value = \"\";",
"clrResults();",
"Height = 0;",
"doCalc = false;",
"alert(\"The maximum value for Height is 244 cm.\\nIf you are specifying a value with a different unit,\\nchange the unit selector first.\");",
"}",
"",
"",
"}",
"",
"}",
"",
"",
"function clrResults(){",
"",
"if (currenttimeout) self.clearTimeout(currenttimeout);",
"",
"with(document.LeanBodyWgtM_form){",
"",
"LBW_param.value = '';",
"",
"",
"}",
"",
"}",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
"",
""].join("\n");
