" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Zoonoses from dogs",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/24/29065/contributors\">",
"     Author",
"    </a>",
