"  <div id=\"mc3k\">",
"   <form action=\"\" id=\"LeanBodyWgtF_form\" name=\"LeanBodyWgtF_form\" onkeydown=\"clrResults();\" onkeypress=\"return validNumberField(event);\" onkeyup=\"LeanBodyWgtF_fx();\" onsubmit=\"return false;\">",
