var title_f28_27_29115="Frequency of maternal blood lead follow-up testing";
var content_f28_27_29115=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F81402&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F81402&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Frequency of maternal blood lead follow-up testing during pregnancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Venous blood lead level (BLL; &micro;g/dL)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Perform follow-up test(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &lt;5",
"       </td>",
"       <td>",
"        None (no follow-up testing is indicated).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5-14",
"       </td>",
"       <td>",
"        Within 1 month. Obtain a maternal BLL* or cord BLL at delivery.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        15-24",
"       </td>",
"       <td>",
"        Within 1 month and then every 2-3 months. Obtain a maternal BLL* or cord BLL at delivery. More frequent testing may be indicated based on risk factor history.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        25-44",
"       </td>",
"       <td>",
"        Within 1-4 weeks and then every month. Obtain a maternal BLL* or cord BLL at delivery.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;45",
"       </td>",
"       <td>",
"        Within 24 hours and then at frequent intervals depending on clinical interventions and trend in BLLs. Consultation with a clinician experienced in the management of pregnant women with BLLs in this range is strongly advised. Obtain a maternal BLL* or cord BLL at delivery.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * If possible, obtain a maternal BLL prior to delivery since BLLs tend to rise over the course of pregnancy.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Ettinger AS, Wengrovitz AG. Guidelines for the Identification and Management of Lead Exposure in Pregnant and Lactating Women. Centers for Disease Control and Prevention. November 2010.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
