var title_f28_27_29116="Causes of anemia";
var content_f28_27_29116=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F53867&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F53867&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of anemia in the adult",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Low mean corpuscular volume (microcytic anemia: MCV &lt;80 fL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iron deficiency anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thalassemic disorders",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anemia of inflammation/anemia of chronic disease (late; uncommon)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sideroblastic anemia (eg, congenital, lead, alcohol, drugs; uncommon)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Copper deficiency, zinc poisoning (rare)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Normal mean corpuscular volume (normocytic anemia: MCV 80 to 100 fL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute blood loss",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Iron deficiency anemia (early)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anemia of inflammation/anemia of chronic disease (eg, infection, inflammation, malignancy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Bone marrow suppression (may also be macrocytic)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Bone marrow invasion (eg, leukoerythroblastic blood picture)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Acquired pure red blood cell aplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Aplastic anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic renal insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Endocrine dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypothyroidism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypopituitarism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Increased mean corpuscular volume (macrocytic anemia: MCV &gt;100 fL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ethanol abuse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Folate deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vitamin B12 deficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Myelodysplastic syndromes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute myeloid leukemias (eg, erythroleukemia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Reticulocytosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hemolytic anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Response to blood loss",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Response to appropriate hematinic (eg, iron, B12, folic acid)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug-induced anemia (eg, Hydroxyurea, AZT, chemotherapeutic agents)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Liver disease",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     This list is not meant to be exhaustive; only the most common causes are mentioned. In addition, two or more of these conditions may be present (eg, combined iron and folate deficiencies), resulting in a misleadingly normal mean corpuscular volume.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
