var script_f28_28_29121=[" /* <![CDATA[ */",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function ChildPughScore_SI_fx() {",
"with(document.ChildPughScore_SI_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 1;",
"}",
"if (cc1[1].checked){",
"Score = Score + 2;",
"}",
"if (cc1[2].checked){",
"Score = Score + 2;",
"}",
"if (cc1[3].checked){",
"Score = Score + 3;",
"}",
"if (cc1[4].checked){",
"Score = Score + 3;",
"}",
"if (cc2[0].checked){",
"Score = Score + 1;",
"}",
"if (cc2[1].checked){",
"Score = Score + 2;",
"}",
"if (cc2[2].checked){",
"Score = Score + 3;",
"}",
"if (cc3[0].checked){",
"Score = Score + 1;",
"}",
"if (cc3[1].checked){",
"Score = Score + 2;",
"}",
"if (cc3[2].checked){",
"Score = Score + 3;",
"}",
"if (cc4[0].checked){",
"Score = Score + 1;",
"}",
"if (cc4[1].checked){",
"Score = Score + 2;",
"}",
"if (cc4[2].checked){",
"Score = Score + 3;",
"}",
"if (cc5[0].checked){",
"Score = Score + 1;",
"}",
"if (cc5[1].checked){",
"Score = Score + 2;",
"}",
"if (cc5[2].checked){",
"Score = Score + 3;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"",
"rrclr();",
"if ((Score >= 5) && (Score <= 6)){ document.getElementById('rr1_1').bgColor = \"#cccccc\";",
"}",
"if ((Score >= 7) && (Score <= 9)){ document.getElementById('rr1_2').bgColor = \"#cccccc\";",
"}",
"if ((Score >= 10) && (Score <= 15)){ document.getElementById('rr1_3').bgColor = \"#cccccc\";",
"}",
"",
"",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"}",
"",
"",
"/* ]]> */"].join("\n");
