" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Timing of first eye examination for retinopathy of prematurity based upon gestational age at birth*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Gestational age at birth",
"        <br/>",
"        (weeks)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chronologic age at initial examination",
"        <br/>",
"        (weeks)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        22",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        8",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        23",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        7",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        24",
"       </td>",
"       <td class=\"centered\">",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        25",
"       </td>",
"       <td class=\"centered\">",
"        6",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        26",
"       </td>",
"       <td class=\"centered\">",
"        4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        27-30",
"       </td>",
"       <td class=\"centered\">",
"        4",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        31-32",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td class=\"centered\">",
"        4",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
