var title_f28_29_29147="Features of AKI versus CKD in children";
var content_f28_29_29147=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F82873&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F82873&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features differentiating acute kidney injury from chronic kidney disease in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Finding",
"       </td>",
"       <td class=\"subtitle1\">",
"        Acute kidney injury (AKI)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Chronic kidney disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum BUN and Cr",
"       </td>",
"       <td>",
"        Progressive rise in BUN and Cr",
"       </td>",
"       <td>",
"        Stable elevated BUN and Cr",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Historical clues",
"       </td>",
"       <td>",
"        Positive history for AKI etiology (eg, recent streptococcal infection: poststreptococcal glomerulonephritis)",
"       </td>",
"       <td>",
"        History of chronic hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Growth",
"       </td>",
"       <td>",
"        Normal growth",
"       </td>",
"       <td>",
"        Impaired growth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone status",
"       </td>",
"       <td>",
"        Normal bones",
"       </td>",
"       <td>",
"        Evidence of renal osteodystrophy: History of fractures, abnormal tibial torsion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Urine sediment",
"       </td>",
"       <td>",
"        No broad urinary casts",
"       </td>",
"       <td>",
"        Broad waxy urinary casts",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematocrit",
"       </td>",
"       <td>",
"        Anemia usually mild",
"       </td>",
"       <td>",
"        Anemia usually severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal ultrasound",
"       </td>",
"       <td>",
"        Normal or enlarged kidney size",
"       </td>",
"       <td>",
"        Small shrunken kidneys",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BUN: blood urea nitrogen, Cr: creatinine",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
