var title_f28_30_29164="Orbital fracture overview";
var content_f28_30_29164=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F61925&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F61925&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Rapid overview of orbital fractures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Initial assessment and stabilization:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Identify life-threatening injuries. Provide cervical spine immobilization when indicated.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Identify globe threatening injuries (orbital hematoma, ruptured globe, optic nerve sheath hematoma).",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Identify injuries that may threaten vision or result in a poor cosmetic outcome. Assessment of visual acuity and slit lamp examination should be performed, whenever possible.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Clinical features - The following clinical features indicate an oribtal fracture with significant associated injury:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Proptosis - orbital hematoma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Widened intracanthal distance - disruption of medial canthal ligament",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Orbital dystopia (affected eye is lower in the horizontal plane than the other) and/or enophthalmos (eye is receded into orbit) - orbital floor fracture",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Limitation of extraocular motility - entrapped muscle",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decreased visual acuity - intraocular injury",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Diagnostic evaluation - Thin cut coronal computed tomography should be obtained for patients with any of the following findings:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Evidence of an orbital fracture on physical examination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Limitation of extraocular motility",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decreased visual acuity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Severe pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Management - Following resuscitation and stabilization, patients with the following injuries require immediate ophthalmologic consultation:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Globe injuries",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Severe vagal symptoms (nausea, vomiting, and bradycardia) secondary to stimulation of the oculocardiac reflex",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Patients with the following symptoms should be evaluated by an ophthalmologist within 24 hours:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Muscle entrapment (either inferior rectus or medial rectus)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Enophthalmos or orbital dystopia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       We suggest that patients with an orbital fracture receive the following:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Follow up with an ophthalmologist within one week",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prophylactic antibiotics for fractures that involve a sinus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Corticosteroids for patients with limitation of extraocular movement",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
