var title_f28_31_29179="Grade of infusion related reactions NCI CTCAE";
var content_f28_31_29179=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F57843&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F57843&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Grading of infusion reactions according to the NCI Common Terminology Criteria for Adverse Events v4.0",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1_single\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1",
"       </td>",
"       <td>",
"        Mild transient reaction; infusion interruption not indicated; intervention not indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2",
"       </td>",
"       <td>",
"        Therapy or infusion interruption indicated but responds promptly to symptomatic treatment (eg, antihistamines, NSAIDs, narcotics, IV fluids); prophylactic medication indicated for less than or equal to 24 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3",
"       </td>",
"       <td>",
"        Prolonged (ie, not rapidly responsive to symptomatic medication and/or brief interruption of infusion); recurrence of symptoms following initial improvement; hospitalization indicated for other clinical sequelae",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4",
"       </td>",
"       <td>",
"        Life-threatening consequences; urgent intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5",
"       </td>",
"       <td>",
"        Death",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     NCI: National Cancer Institute; NSAIDs: nonsteroidal anti-inflammatory drugs.",
"    </div>",
"    <div class=\"reference\">",
"     With permission from: Common Terminology Criteria for Adverse Events, version 4.0, June 2010, National Institutes of Health, National Cancer Institute, file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
