var title_f28_31_29180="Appropriate on scene pediatric prehospital interventions";
var content_f28_31_29180=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F83778&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F83778&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Appropriate on scene pediatric prehospital interventions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Assessment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Initiate cardiac monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Measure pulse oximetry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Airway and cervical spine immobilization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Open and maintain the airway",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suction the airway to remove secretions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Apply cervical collar and back board immobilization for suspected spinal injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer racemic or L-epinephrine for severe inspiratory stridor (eg, severe croup)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Place an advanced airway, if unable to maintain airway by other means",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Breathing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform&nbsp;bag-mask ventilation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer beta agonist therapy for reactive airway disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform needle decompression for tension pneumothorax",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Circulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Control external hemorrhage (eg, penetrating trauma to extremity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide cardiopulmonary resuscitation if indicated - at least three cycles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform synchronized cardioversion and defibrillation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obtain intravenous or intraosseous access if benefit outweighs delay in transport",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer medications indicated for cardiopulmonary arrest - at least one cycle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer IV fluids for shock",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Disability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer glucose for hypoglycemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer medications to control seizures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Splint extremities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform eye irrigation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer medications (eg, epinephrine, diphenhydramine, and steroids) for allergic or anaphylactic reactions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer hydroxocobalamin for cyanide poisoning (eg, home fires)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer pain medication",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
