" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Initial evaluation and diagnosis of Hodgkin lymphoma in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/33/29206/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/33/29206/contributors\">",
"     Peter M Mauch, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/33/29206/contributors\">",
