" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical features of cerebral palsy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/33/29209/contributors\">",
"     Author",
"    </a>",
