var title_f28_34_29229="Cowden syndrome testing and clinical criteria of the NCCN";
var content_f28_34_29229=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F72070&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F72070&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cowden syndrome clinical criteria and testing guidelines from the National Comprehensive Cancer Network (NCCN)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Testing criteria",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Individual from a family with a known PTEN mutation",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Individual with a personal history of:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Bannayan-Riley-Ruvalcaba syndrome",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Adult Lhermitte-Duclos disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Autism spectrum disorder AND macrocephaly",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Two or more biopsy-proven trichilemmomas",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Two or more major criteria (one macrocephaly)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Three major criteria without macrocephaly",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - One major and three or more minor criteria",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            OR",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Four or more minor criteria",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            At-risk individual:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - With a relative who has a clinical diagnosis of Cowden syndrome or Bannayan-Riley-Ruvalcaba syndrome for whom testing has not been performed",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            AND",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            - Who has any one major criterion or two minor criteria",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Clinical criteria",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Major",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Breast cancer",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2_start\">",
"            Mucocutaneous lesions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            - One biopsy-proven trichilemmoma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            - Multiple palmoplantar keratoses",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            - Multifocal or extensive oral mucosal papillomatosis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            - Multiple cutaneous facial papules (often verrucous)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"sublist2\">",
"            - Macular pigmentation of glans penis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Macroencephaly (megalocephaly, ie, &ge;97th percentile)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Endometrial cancer",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Non-medullary thyroid cancer",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Multiple GI tract hamartomas or ganglioneuromas",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2_single\">",
"            Minor",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Other thyroid lesions (adenoma, nodule, goiter)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Mental retardation (IQ &le;75)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Autism spectrum disorder",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Single GI tract hamartoma or ganglioneuroma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Fibrocystic disease of the breast",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Lipomas",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Fibromas",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Renal cell carcinoma",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"indent1\">",
"            Uterine fibroids",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Tan MH, Mester J, Peterson C, et al. A clinical scoring system for selection of patients for PTEN mutation testing is proposed on the basis of a prospective study of 3042 probands. Am J Hum Genet 2011; 88:42 and NCCN Guidelines, Version 1.2011.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
