" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pharmacotherapy for schizophrenia: Acute and maintenance phase treatment",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/35/29241/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/35/29241/contributors\">",
"     T. Scott Stroup, MD, MPH",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/35/29241/contributors\">",
"     Stephen Marder, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/35/29241/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/35/29241/contributors\">",
"     Murray B Stein, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/35/29241/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?28/35/29241/contributors\">",
"     Richard Hermann, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
