" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   The spinocerebellar ataxias",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/35/29242/contributors\">",
