var title_f28_35_29244="Severity of pneumonia in infants and children";
var content_f28_35_29244=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F72015&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F72015&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Severity of community acquired pneumonia in infants and children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical features of mild pneumonia",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features of severe pneumonia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Temperature &lt;38.5&deg;C (101.3&deg;F)",
"       </td>",
"       <td>",
"        Temperature &ge;38.5&deg;C (101.3&deg;F)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Mild or absent respiratory distress:",
"        </p>",
"        <ul>",
"         <li>",
"          Increased RR, but less than the age-specific RR that defines moderate to severe respiratory distress",
"         </li>",
"         <li>",
"          Mild or absent retractions",
"         </li>",
"         <li>",
"          No grunting",
"         </li>",
"         <li>",
"          No nasal flaring",
"         </li>",
"         <li>",
"          No apnea",
"         </li>",
"         <li>",
"          Mild shortness of breath",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <p>",
"         Moderate to severe respiratory distress:",
"        </p>",
"        <ul>",
"         <li>",
"          RR &gt;70 breaths/minute for infants; RR &gt;50 breaths/minute for older children",
"         </li>",
"         <li>",
"          Moderate/severe suprasternal, intercostal, or subcostal retractions (&lt;12 months)",
"         </li>",
"         <li>",
"          Severe difficulty breathing (&ge;12 months)",
"         </li>",
"         <li>",
"          Grunting",
"         </li>",
"         <li>",
"          Nasal flaring",
"         </li>",
"         <li>",
"          Apnea",
"         </li>",
"         <li>",
"          Significant shortness of breath",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal color",
"       </td>",
"       <td>",
"        Cyanosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal mental status",
"       </td>",
"       <td>",
"        Altered mental status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normoxemia (oxygen saturation &ge;92 percent in room air)",
"       </td>",
"       <td>",
"        Hypoxemia (sustained oxygen saturation &lt;90 percent in room air at sea level)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal feeding (infants); no vomiting",
"       </td>",
"       <td>",
"        Not feeding (infants) or signs of dehydration (older children)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal heart rate",
"       </td>",
"       <td>",
"        Tachycardia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Capillary refill &lt;2 seconds",
"       </td>",
"       <td>",
"        Capillary refill &ge;2 seconds",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     RR: respiratory rate.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Bradley JS, Byington CL, Shah SS, et al. The management of community-acquired pneumonia in infants and children older than 3 months of age: Clinical practice guidelines by the Pediatric Infectious Diseases Society and the Infectious Diseases Society of America. Clin Infect Dis 2011; 53:e25.",
"      </li>",
"      <li>",
"       Harris M, Clark J, Coote N, et al. British Thoracic Society guidelines for the management of community acquired pneumonia in children: update 2011. Thorax 2011; 66:ii1.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
