var title_f28_36_29262="Guidelines for timing of delivery after 34 weeks gestation";
var content_f28_36_29262=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F64814&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F64814&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Consensus guidelines for timing of delivery for maternal, fetal, and placental conditions after 34 weeks gestation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gestational age* at delivery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Placental and uterine issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Placenta previa",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        36-37 wk (grade B)",
"        <sup>",
"         &loz;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Suspected placenta accreta, increta, or percreta with placenta previa",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        34-35 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prior classical cesarean (upper segment uterine incision)",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        36-37 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prior myomectomy necessitating cesarean delivery",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        37-38 wk (may require earlier delivery, similar to prior classical cesarean, in situations with more extensive or complicated myomectomy) (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Fetal issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fetal growth restriction - singleton",
"       </td>",
"       <td>",
"        38-39 wk:",
"        <ul>",
"         <li>",
"          Otherwise uncomplicated, no concurrent findings (grade B)",
"         </li>",
"        </ul>",
"        <br/>",
"        34-37 wk:",
"        <ul>",
"         <li>",
"          Concurrent conditions (oligohydramnios, abnormal Doppler studies, maternal risk factors, co-morbidity) (grade B)",
"         </li>",
"        </ul>",
"        <br/>",
"        Expeditious delivery regardless of gestational age:",
"        <ul>",
"         <li>",
"          Persistent abnormal fetal surveillance suggesting imminent fetal jeopardy",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fetal growth restriction - twin gestation",
"       </td>",
"       <td>",
"        36-37 wk:",
"        <ul>",
"         <li>",
"          Dichorionic-diamniotic twins with isolated fetal growth restriction (grade B)",
"         </li>",
"        </ul>",
"        <br/>",
"        32-34 wk:",
"        <ul>",
"         <li>",
"          Monochorionic-diamniotic twins with isolated fetal growth restriction (grade B)",
"         </li>",
"         <li>",
"          Concurrent conditions (oligohydramnios, abnormal Doppler studies, maternal risk factors, co-morbidity) (grade B)",
"         </li>",
"        </ul>",
"        <br/>",
"        Expeditious delivery regardless of gestational age:",
"        <ul>",
"         <li>",
"          Persistent abnormal fetal surveillance suggesting imminent fetal jeopardy",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fetal congenital malformations",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        34-39 wk:",
"        <ul>",
"         <li>",
"          Suspected worsening of fetal organ damage (grade B)",
"         </li>",
"         <li>",
"          Potential for fetal intracranial hemorrhage (eg, vein of Galen aneurysm, neonatal alloimmune thrombocytopenia) (grade B)",
"         </li>",
"         <li>",
"          When delivery prior to labor is preferred (eg, EXIT procedure) (grade B)",
"         </li>",
"         <li>",
"          Previous fetal intervention (grade B)",
"         </li>",
"         <li>",
"          Concurrent maternal disease (eg, preeclampsia, chronic hypertension) (grade B)",
"         </li>",
"         <li>",
"          Potential for adverse maternal effect from fetal condition (grade B)",
"         </li>",
"        </ul>",
"        <br/>",
"        Expeditious delivery regardless of gestational age:",
"        <ul>",
"         <li>",
"          When intervention is expected to be beneficial (grade B)",
"         </li>",
"         <li>",
"          Fetal complications develop (abnormal fetal surveillance, new-onset hydrops fetalis, progressive or new-onset organ injury) (grade B)",
"         </li>",
"         <li>",
"          Maternal complications develop (mirror syndrome) (grade B)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Multiple gestations: dichorionic-diamniotic",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        38 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Multiple gestations: monochorionic-diamniotic",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        34-37 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Multiple gestations: dichorionic-diamniotic or monochorionic-diamniotic with single fetal death",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        If occurs at or after 34 wk, consider delivery (recommendation limited to pregnancies at or after 34 wk; if occurs before 34 wk, individualize based on concurrent maternal or fetal conditions) (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Multiple gestations: monochorionic-monoamniotic",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        32-34 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Multiple gestations: Monochorionic-monoamniotic with single fetal death",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Consider delivery; individualized according to gestational age and concurrent complications (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Oligohydramnios - isolated and persistent",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        36-37 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Maternal issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic hypertension - no medications",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        38-39 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic hypertension - controlled on medication",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        37-39 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Chronic hypertension - difficult to control (requiring frequent medication adjustments)",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        36-37 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gestational hypertension",
"        <sup>",
"         &sect;",
"        </sup>",
"       </td>",
"       <td>",
"        37-38 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Preeclampsia - severe",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        At diagnosis (recommendation limited to pregnancies at or after 34 wk) (grade C)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Preeclampsia - mild",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        37 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diabetes - pregestational well controlled",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        LPTB or ETB not recommended (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diabetes - pregestational with vascular disease",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        37-39 wk (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diabetes - pregestational poorly controlled",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        34-39 wk (individualized to situation) (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diabetes - gestational well controlled on diet",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        LPTB or ETB not recommended (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diabetes - gestational well controlled on medication",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        LPTB or ETB not recommended (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Diabetes - gestational poorly controlled on medication",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        34-39 wk (individualized to situation) (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Obstetric issues",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Prior stillbirth-unexplained",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        <p>",
"         LPTB or ETB not recommended (grade B)",
"        </p>",
"        <p>",
"         Consider amniocentesis for fetal pulmonary maturity if delivery planned at less than 39 wk (grade C)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Spontaneous preterm birth: preterm premature rupture of membranes",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        34 wk (recommendation limited to pregnancies at or after 34 wk) (grade B)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Spontaneous preterm birth: active preterm labor",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"       <td>",
"        Delivery if progressive labor or additional maternal or fetal indication (grade B)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LPTB: late preterm birth at 34 0/7 weeks through 36 6/7 weeks; ETB: early-term birth at 37 0/7 weeks through 38 6/7 weeks.",
"     <br>",
"      * Gestational age is in completed weeks; thus, 34 weeks includes 34 0/7 weeks through 34 6/7 weeks.",
"      <br>",
"       &Delta; Uncomplicated, thus no fetal growth restriction, superimposed preeclampsia, etc. If these are present, then the complicating conditions take precedence, and earlier delivery may be indicated.",
"       <br>",
"        <span class=\"lozenge\">",
"         &loz;",
"        </span>",
"        Grade of recommendations are based on the following: recommendations or conclusions or both are based on good and consistent scientific evidence (A); limited or inconsistent scientific evidence (B); primarily consensus and expert opinion (C). The recommendations regarding expeditious delivery for imminent fetal jeopardy were not given a grade. The recommendation regarding severe preeclampsia is based largely on expert opinion; however, higher-level evidence is not likely to be forthcoming because this condition is believed to carry significant maternal risk with limited potential fetal benefit from expectant management after 34 weeks.",
"        <br>",
"         &sect; Maintenance antihypertensive therapy should not be used to treat gestational hypertension.",
"        </br>",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Spong CY, Mercer BM, D'Alton M, et al. Timing of indicated late-preterm and early-term birth. Obstet and Gynecol 2011; 118:323. Copyright &copy; 2011 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
