" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Iliac artery aneurysm",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?28/39/29304/contributors\">",
